package org.beng183.codons;

import static org.junit.Assert.assertEquals;

import java.util.ArrayList;
import java.util.Collection;
import java.util.List;

import org.biojava.bio.structure.Structure;
import org.biojava.bio.structure.scop.ScopDomain;
import org.junit.Test;
import org.junit.runner.RunWith;
import org.junit.runners.Parameterized;
import org.junit.runners.Parameterized.Parameters;

/**
 * A test for {@link DataRetrievalSystem}.
 * @author dmyersturnbull
 */
@RunWith(Parameterized.class)
public class DataRetrievalSystemTest {

	private static final double PRECISION = Math.pow(2, -16);

	private final DataRetrievalSystem retrieval;

	public DataRetrievalSystemTest(DataRetrievalSystem retrieval) {
		this.retrieval = retrieval;
	}

	@Test
	public final void testMapping() throws LoadException {
		// d1hiva_ = 1A30 = POL_HV1H2 = 155348
		// this is also 1HIV, but there are several PDB entries for POL_HV1H2
		Structure structure = retrieval.getStructureFromGeneName("155348");
		assertEquals("1A30", structure.getPDBCode());
		List<ScopDomain> domains = retrieval.getDomainsFromGeneName("155348");
		assertEquals("d1a30a_", domains.get(0).getScopId());
	}

	@Test
	public final void testGeneticSequence() throws LoadException {
		String sequence = retrieval.getGeneticSequenceFromName("155348");
		assertEquals("", sequence);
	}

	@Parameters
	public static Collection<Object[]> getInstances() {
		List<Object[]> list = new ArrayList<>();
		list.add(new Object[] {new SimpleDataRetrievalSystem()});
		return list;
	}
}
